import { API_BASE } from '../config/env';
import axios from 'axios';
// action types
// action tipleri ilgili durumlarda dispatch edilir
export const FETCH_MOVIES = "FETCHED_MOVIES";
export const FETCH_MOVIES_ERROR = "FETCHED_MOVIES_ERROR";

//yazılan action reducer'a dispatch edilecek
export function fetchMovies(){
     return dispatch => {
      dispatch({
        type: "ETCH_MOVIES",
        payload: axios.get(`${API_BASE}/movies`)
            .then(result => result.data)
      })
    }
}
