// MOVIES REDUCER
import { FETCH_MOVIES, FETCH_MOVIES_ERROR } from '../actions/movies';

const initialState = {
     fetching: false,
     movies: [],
     error: {}
};

export default (state =[], action) => {
    switch(action.type){
        case FETCH_MOVIES:
          return {
            ...state,

            movies: action.payload
        };
        case FETCH_MOVIES_ERROR:
        return {
            ...state,
            error: action.payload
        };
       default:
         return state;
    }
}
