import {API_BASE} from '../config/env';
import axios from 'axios';

export const FETCHED_MOVIES_FULLFILLED = "FETCHED_MOVIES_FULLFILLED";
export const FETCHED_MOVIES_REJECTED = "FETCH_MOVIES_REJECT";

export function fetchMovies(){
   return dispatch => {
      dispatch({
        type:"FETCHED_MOVIES_FULLFILLED",
        payload:axios.get(`${API_BASE}/movies`)
                .then(result=>result.data)
        })
   }
}
