// MOVIES REDUCER
import { NEW_MOVIE,
         NEW_MOVIE_ERROR 
} from '../actions/newMovie';

const initialState = {
     fetching: false,
     done: false,
     movies: [],
     error: {}
};

export default (state =initialState, action) => {
    switch(action.type){
        case NEW_MOVIE:
          return {
            ...state,
            fetching: true,
            movies: action.payload,
            done: true,

        };
        case NEW_MOVIE_ERROR:
        return {
            ...state,
            error: action.payload,
            fetching: false,
            done: false
        };
       default:
         return state;
    }
}
