import { ETCHED_MOVIES_FULLFILLED , FETCHED_MOVIES_ERROR } from '../actions/movies';

const initialState = {
   fetching: false,
   fetched: false,
   movies : [],
   error: {}
};

export default (state = initialState, action) => {
   switch(action.type){
       case FETCHED_MOVIES:
       return {
           ...state,
           movies: action.payload
       }
       case FETCHED_MOVIES_ERROR:
       return {
           ...state,
           movies: action.payload
       }
       default:
       return state;
   }
}
