   switch (actionType){
       case FETCHED_MOVIES_FULLFILLED:
       console.log('SWITCH ACTION TYPE' , action.type, "MOVIES" , action.payload);
       return {
           ...state,
           movies: action.payload
       };
       break;
       case  FETCHED_MOVIES_REJECTED:
