import React from 'react';
import PropTypes from 'prop-types';

// Stateless Component
// Gelen movies propuna göre MoviesList şekille
const MoviesList = ({ movies}) => {
   const emptyMessage = (
      <p>There are no movies yet. </p>
   );
   // Hatanın Alınmadığı Durumlarda Filmler Listelenecek
   const moviesList = (
   <div>
       {
         movies.error.response ?  <h3> Error Retrieving data ! </h3>: <h3>No Error</h3>
       }
   </div>

   );
   console.log('MoviesList',movies);
   return (

      <div>
          {movies.length === 0 ? emptyMessage : moviesList }
      </div>
   );
};

// backend service'den gelen movie objesi ve movies objesi içerisindeki movie listi geçirilecek
// bu nedenle yapılandırma gerekiyor

MoviesList.propTypes = {
    movies: PropTypes.shape({
      movies: PropTypes.array.isRequired,
    }).isRequired
};

export default MoviesList;
